import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';

const GITHUB_TOKEN = process.env.GITHUB_TOKEN;
const GITHUB_BASE_URL = 'https://api.github.com';

if (!GITHUB_TOKEN) {
  console.error('Error: GITHUB_TOKEN environment variable is required');
  process.exit(1);
}

const server = new Server({ name: 'github-mcp', version: '1.0.0' }, { capabilities: { tools: {} } });

async function githubRequest(endpoint: string, method: string = 'GET', body?: unknown) {
  const response = await fetch(`${GITHUB_BASE_URL}${endpoint}`, {
    method,
    headers: {
      'Authorization': `Bearer ${GITHUB_TOKEN}`,
      'Accept': 'application/vnd.github.v3+json',
      'Content-Type': 'application/json',
    },
    body: body ? JSON.stringify(body) : undefined,
  });
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.message || 'GitHub API error');
  }
  return response.json();
}

const tools = [
  { name: 'get_repository', description: 'Get repository details', inputSchema: { type: 'object', properties: { owner: { type: 'string' }, repo: { type: 'string' } }, required: ['owner', 'repo'] } },
  { name: 'list_repositories', description: 'List repositories for a user or org', inputSchema: { type: 'object', properties: { username: { type: 'string' }, type: { type: 'string', enum: ['all', 'owner', 'member'] }, per_page: { type: 'number' } }, required: ['username'] } },
  { name: 'create_repository', description: 'Create a new repository', inputSchema: { type: 'object', properties: { name: { type: 'string' }, description: { type: 'string' }, private: { type: 'boolean' }, auto_init: { type: 'boolean' } }, required: ['name'] } },
  { name: 'get_issue', description: 'Get an issue by number', inputSchema: { type: 'object', properties: { owner: { type: 'string' }, repo: { type: 'string' }, issue_number: { type: 'number' } }, required: ['owner', 'repo', 'issue_number'] } },
  { name: 'list_issues', description: 'List issues in a repository', inputSchema: { type: 'object', properties: { owner: { type: 'string' }, repo: { type: 'string' }, state: { type: 'string', enum: ['open', 'closed', 'all'] }, per_page: { type: 'number' } }, required: ['owner', 'repo'] } },
  { name: 'create_issue', description: 'Create a new issue', inputSchema: { type: 'object', properties: { owner: { type: 'string' }, repo: { type: 'string' }, title: { type: 'string' }, body: { type: 'string' }, labels: { type: 'array', items: { type: 'string' } } }, required: ['owner', 'repo', 'title'] } },
  { name: 'get_pull_request', description: 'Get a pull request', inputSchema: { type: 'object', properties: { owner: { type: 'string' }, repo: { type: 'string' }, pull_number: { type: 'number' } }, required: ['owner', 'repo', 'pull_number'] } },
  { name: 'create_pull_request', description: 'Create a pull request', inputSchema: { type: 'object', properties: { owner: { type: 'string' }, repo: { type: 'string' }, title: { type: 'string' }, head: { type: 'string' }, base: { type: 'string' }, body: { type: 'string' } }, required: ['owner', 'repo', 'title', 'head', 'base'] } },
  { name: 'get_file_contents', description: 'Get file contents', inputSchema: { type: 'object', properties: { owner: { type: 'string' }, repo: { type: 'string' }, path: { type: 'string' }, ref: { type: 'string' } }, required: ['owner', 'repo', 'path'] } },
  { name: 'search_code', description: 'Search code', inputSchema: { type: 'object', properties: { q: { type: 'string' }, per_page: { type: 'number' } }, required: ['q'] } },
  { name: 'search_issues', description: 'Search issues', inputSchema: { type: 'object', properties: { q: { type: 'string' }, per_page: { type: 'number' } }, required: ['q'] } },
  { name: 'get_user', description: 'Get user profile', inputSchema: { type: 'object', properties: { username: { type: 'string' } }, required: ['username'] } },
];

server.setRequestHandler(ListToolsRequestSchema, async () => ({ tools }));

server.setRequestHandler(CallToolRequestSchema, async (request) => {
  const { name, arguments: args } = request.params;
  try {
    let result;
    const a = args as Record<string, unknown>;
    switch (name) {
      case 'get_repository': result = await githubRequest(`/repos/${a.owner}/${a.repo}`); break;
      case 'list_repositories': result = await githubRequest(`/users/${a.username}/repos?type=${a.type || 'all'}&per_page=${a.per_page || 30}`); break;
      case 'create_repository': result = await githubRequest('/user/repos', 'POST', a); break;
      case 'get_issue': result = await githubRequest(`/repos/${a.owner}/${a.repo}/issues/${a.issue_number}`); break;
      case 'list_issues': result = await githubRequest(`/repos/${a.owner}/${a.repo}/issues?state=${a.state || 'open'}&per_page=${a.per_page || 30}`); break;
      case 'create_issue': result = await githubRequest(`/repos/${a.owner}/${a.repo}/issues`, 'POST', { title: a.title, body: a.body, labels: a.labels }); break;
      case 'get_pull_request': result = await githubRequest(`/repos/${a.owner}/${a.repo}/pulls/${a.pull_number}`); break;
      case 'create_pull_request': result = await githubRequest(`/repos/${a.owner}/${a.repo}/pulls`, 'POST', { title: a.title, head: a.head, base: a.base, body: a.body }); break;
      case 'get_file_contents': result = await githubRequest(`/repos/${a.owner}/${a.repo}/contents/${a.path}${a.ref ? `?ref=${a.ref}` : ''}`); break;
      case 'search_code': result = await githubRequest(`/search/code?q=${encodeURIComponent(a.q as string)}&per_page=${a.per_page || 30}`); break;
      case 'search_issues': result = await githubRequest(`/search/issues?q=${encodeURIComponent(a.q as string)}&per_page=${a.per_page || 30}`); break;
      case 'get_user': result = await githubRequest(`/users/${a.username}`); break;
      default: throw new Error(`Unknown tool: ${name}`);
    }
    return { content: [{ type: 'text', text: JSON.stringify(result, null, 2) }] };
  } catch (error) {
    return { content: [{ type: 'text', text: `Error: ${error instanceof Error ? error.message : 'Unknown error'}` }], isError: true };
  }
});

async function main() {
  const transport = new StdioServerTransport();
  await server.connect(transport);
}

main().catch(console.error);
