import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';

const LINEAR_API_KEY = process.env.LINEAR_API_KEY;
const LINEAR_BASE_URL = 'https://api.linear.app/graphql';

if (!LINEAR_API_KEY) {
  console.error('Error: LINEAR_API_KEY environment variable is required');
  process.exit(1);
}

const server = new Server({ name: 'linear-mcp', version: '1.0.0' }, { capabilities: { tools: {} } });

async function linearGraphQL(query: string, variables?: Record<string, unknown>) {
  const response = await fetch(LINEAR_BASE_URL, {
    method: 'POST',
    headers: {
      'Authorization': LINEAR_API_KEY,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ query, variables }),
  });
  const result = await response.json();
  if (result.errors) throw new Error(result.errors[0].message);
  return result.data;
}

const tools = [
  { name: 'create_issue', description: 'Create a new issue', inputSchema: { type: 'object', properties: { title: { type: 'string' }, description: { type: 'string' }, teamId: { type: 'string' }, priority: { type: 'number' }, assigneeId: { type: 'string' }, labelIds: { type: 'array', items: { type: 'string' } } }, required: ['title', 'teamId'] } },
  { name: 'get_issue', description: 'Get an issue', inputSchema: { type: 'object', properties: { id: { type: 'string' } }, required: ['id'] } },
  { name: 'update_issue', description: 'Update an issue', inputSchema: { type: 'object', properties: { id: { type: 'string' }, title: { type: 'string' }, description: { type: 'string' }, stateId: { type: 'string' }, priority: { type: 'number' } }, required: ['id'] } },
  { name: 'list_issues', description: 'List issues', inputSchema: { type: 'object', properties: { teamId: { type: 'string' }, first: { type: 'number' }, filter: { type: 'object' } } } },
  { name: 'list_projects', description: 'List projects', inputSchema: { type: 'object', properties: { first: { type: 'number' } } } },
  { name: 'get_project', description: 'Get a project', inputSchema: { type: 'object', properties: { id: { type: 'string' } }, required: ['id'] } },
  { name: 'list_teams', description: 'List teams', inputSchema: { type: 'object', properties: {} } },
  { name: 'list_cycles', description: 'List cycles', inputSchema: { type: 'object', properties: { teamId: { type: 'string' }, first: { type: 'number' } } } },
  { name: 'create_comment', description: 'Create a comment', inputSchema: { type: 'object', properties: { issueId: { type: 'string' }, body: { type: 'string' } }, required: ['issueId', 'body'] } },
  { name: 'search_issues', description: 'Search issues', inputSchema: { type: 'object', properties: { query: { type: 'string' }, first: { type: 'number' } }, required: ['query'] } },
];

server.setRequestHandler(ListToolsRequestSchema, async () => ({ tools }));

server.setRequestHandler(CallToolRequestSchema, async (request) => {
  const { name, arguments: args } = request.params;
  try {
    let result;
    const a = args as Record<string, unknown>;
    switch (name) {
      case 'create_issue':
        result = await linearGraphQL(`mutation CreateIssue($input: IssueCreateInput!) { issueCreate(input: $input) { success issue { id identifier title url } } }`, { input: a });
        break;
      case 'get_issue':
        result = await linearGraphQL(`query GetIssue($id: String!) { issue(id: $id) { id identifier title description state { name } priority assignee { name } labels { nodes { name } } } }`, { id: a.id });
        break;
      case 'update_issue':
        const { id, ...updateInput } = a;
        result = await linearGraphQL(`mutation UpdateIssue($id: String!, $input: IssueUpdateInput!) { issueUpdate(id: $id, input: $input) { success issue { id identifier title } } }`, { id, input: updateInput });
        break;
      case 'list_issues':
        result = await linearGraphQL(`query ListIssues($first: Int) { issues(first: $first) { nodes { id identifier title state { name } priority assignee { name } } } }`, { first: a.first || 50 });
        break;
      case 'list_projects':
        result = await linearGraphQL(`query ListProjects($first: Int) { projects(first: $first) { nodes { id name state } } }`, { first: a.first || 50 });
        break;
      case 'get_project':
        result = await linearGraphQL(`query GetProject($id: String!) { project(id: $id) { id name description state startDate targetDate } }`, { id: a.id });
        break;
      case 'list_teams':
        result = await linearGraphQL(`query { teams { nodes { id name key } } }`);
        break;
      case 'list_cycles':
        result = await linearGraphQL(`query ListCycles($first: Int) { cycles(first: $first) { nodes { id name startsAt endsAt } } }`, { first: a.first || 10 });
        break;
      case 'create_comment':
        result = await linearGraphQL(`mutation CreateComment($input: CommentCreateInput!) { commentCreate(input: $input) { success comment { id body } } }`, { input: { issueId: a.issueId, body: a.body } });
        break;
      case 'search_issues':
        result = await linearGraphQL(`query SearchIssues($query: String!, $first: Int) { searchIssues(query: $query, first: $first) { nodes { id identifier title } } }`, { query: a.query, first: a.first || 20 });
        break;
      default: throw new Error(`Unknown tool: ${name}`);
    }
    return { content: [{ type: 'text', text: JSON.stringify(result, null, 2) }] };
  } catch (error) {
    return { content: [{ type: 'text', text: `Error: ${error instanceof Error ? error.message : 'Unknown error'}` }], isError: true };
  }
});

async function main() {
  const transport = new StdioServerTransport();
  await server.connect(transport);
}

main().catch(console.error);
