import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';

const NOTION_TOKEN = process.env.NOTION_TOKEN;
const NOTION_BASE_URL = 'https://api.notion.com/v1';

if (!NOTION_TOKEN) {
  console.error('Error: NOTION_TOKEN environment variable is required');
  process.exit(1);
}

const server = new Server({ name: 'notion-mcp', version: '1.0.0' }, { capabilities: { tools: {} } });

async function notionRequest(endpoint: string, method: string = 'GET', body?: unknown) {
  const response = await fetch(`${NOTION_BASE_URL}${endpoint}`, {
    method,
    headers: {
      'Authorization': `Bearer ${NOTION_TOKEN}`,
      'Notion-Version': '2022-06-28',
      'Content-Type': 'application/json',
    },
    body: body ? JSON.stringify(body) : undefined,
  });
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.message || 'Notion API error');
  }
  return response.json();
}

const tools = [
  { name: 'get_page', description: 'Get a page by ID', inputSchema: { type: 'object', properties: { page_id: { type: 'string' } }, required: ['page_id'] } },
  { name: 'create_page', description: 'Create a new page', inputSchema: { type: 'object', properties: { parent: { type: 'object' }, properties: { type: 'object' }, children: { type: 'array' } }, required: ['parent', 'properties'] } },
  { name: 'update_page', description: 'Update page properties', inputSchema: { type: 'object', properties: { page_id: { type: 'string' }, properties: { type: 'object' } }, required: ['page_id', 'properties'] } },
  { name: 'query_database', description: 'Query a database', inputSchema: { type: 'object', properties: { database_id: { type: 'string' }, filter: { type: 'object' }, sorts: { type: 'array' }, page_size: { type: 'number' } }, required: ['database_id'] } },
  { name: 'get_block', description: 'Get a block by ID', inputSchema: { type: 'object', properties: { block_id: { type: 'string' } }, required: ['block_id'] } },
  { name: 'get_block_children', description: 'Get block children', inputSchema: { type: 'object', properties: { block_id: { type: 'string' }, page_size: { type: 'number' } }, required: ['block_id'] } },
  { name: 'append_block_children', description: 'Append children to a block', inputSchema: { type: 'object', properties: { block_id: { type: 'string' }, children: { type: 'array' } }, required: ['block_id', 'children'] } },
  { name: 'search', description: 'Search pages and databases', inputSchema: { type: 'object', properties: { query: { type: 'string' }, filter: { type: 'object' }, page_size: { type: 'number' } } } },
  { name: 'get_user', description: 'Get user information', inputSchema: { type: 'object', properties: { user_id: { type: 'string' } }, required: ['user_id'] } },
];

server.setRequestHandler(ListToolsRequestSchema, async () => ({ tools }));

server.setRequestHandler(CallToolRequestSchema, async (request) => {
  const { name, arguments: args } = request.params;
  try {
    let result;
    const a = args as Record<string, unknown>;
    switch (name) {
      case 'get_page': result = await notionRequest(`/pages/${a.page_id}`); break;
      case 'create_page': result = await notionRequest('/pages', 'POST', a); break;
      case 'update_page': result = await notionRequest(`/pages/${a.page_id}`, 'PATCH', { properties: a.properties }); break;
      case 'query_database': result = await notionRequest(`/databases/${a.database_id}/query`, 'POST', { filter: a.filter, sorts: a.sorts, page_size: a.page_size }); break;
      case 'get_block': result = await notionRequest(`/blocks/${a.block_id}`); break;
      case 'get_block_children': result = await notionRequest(`/blocks/${a.block_id}/children?page_size=${a.page_size || 100}`); break;
      case 'append_block_children': result = await notionRequest(`/blocks/${a.block_id}/children`, 'PATCH', { children: a.children }); break;
      case 'search': result = await notionRequest('/search', 'POST', { query: a.query, filter: a.filter, page_size: a.page_size }); break;
      case 'get_user': result = await notionRequest(`/users/${a.user_id}`); break;
      default: throw new Error(`Unknown tool: ${name}`);
    }
    return { content: [{ type: 'text', text: JSON.stringify(result, null, 2) }] };
  } catch (error) {
    return { content: [{ type: 'text', text: `Error: ${error instanceof Error ? error.message : 'Unknown error'}` }], isError: true };
  }
});

async function main() {
  const transport = new StdioServerTransport();
  await server.connect(transport);
}

main().catch(console.error);
