import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';

const RESEND_API_KEY = process.env.RESEND_API_KEY;
const RESEND_BASE_URL = 'https://api.resend.com';

if (!RESEND_API_KEY) {
  console.error('Error: RESEND_API_KEY environment variable is required');
  process.exit(1);
}

const server = new Server({ name: 'resend-mcp', version: '1.0.0' }, { capabilities: { tools: {} } });

async function resendRequest(endpoint: string, method: string = 'GET', body?: unknown) {
  const response = await fetch(`${RESEND_BASE_URL}${endpoint}`, {
    method,
    headers: {
      'Authorization': `Bearer ${RESEND_API_KEY}`,
      'Content-Type': 'application/json',
    },
    body: body ? JSON.stringify(body) : undefined,
  });
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.message || 'Resend API error');
  }
  return response.json();
}

const tools = [
  { name: 'send_email', description: 'Send an email', inputSchema: { type: 'object', properties: { from: { type: 'string', description: 'Sender email' }, to: { type: 'array', items: { type: 'string' }, description: 'Recipients' }, subject: { type: 'string' }, html: { type: 'string', description: 'HTML body' }, text: { type: 'string', description: 'Plain text body' }, cc: { type: 'array', items: { type: 'string' } }, bcc: { type: 'array', items: { type: 'string' } } }, required: ['from', 'to', 'subject'] } },
  { name: 'get_email', description: 'Get email details', inputSchema: { type: 'object', properties: { email_id: { type: 'string' } }, required: ['email_id'] } },
  { name: 'list_emails', description: 'List sent emails', inputSchema: { type: 'object', properties: { limit: { type: 'number' }, cursor: { type: 'string' } } } },
  { name: 'create_domain', description: 'Add a domain', inputSchema: { type: 'object', properties: { name: { type: 'string' }, region: { type: 'string', enum: ['us-east-1', 'eu-west-1', 'sa-east-1'] } }, required: ['name'] } },
  { name: 'list_domains', description: 'List domains', inputSchema: { type: 'object', properties: {} } },
  { name: 'verify_domain', description: 'Verify domain', inputSchema: { type: 'object', properties: { domain_id: { type: 'string' } }, required: ['domain_id'] } },
];

server.setRequestHandler(ListToolsRequestSchema, async () => ({ tools }));

server.setRequestHandler(CallToolRequestSchema, async (request) => {
  const { name, arguments: args } = request.params;
  try {
    let result;
    const a = args as Record<string, unknown>;
    switch (name) {
      case 'send_email': result = await resendRequest('/emails', 'POST', a); break;
