import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';

const SUPABASE_URL = process.env.SUPABASE_URL;
const SUPABASE_KEY = process.env.SUPABASE_KEY;

if (!SUPABASE_URL || !SUPABASE_KEY) {
  console.error('Error: SUPABASE_URL and SUPABASE_KEY environment variables are required');
  process.exit(1);
}

const server = new Server({ name: 'supabase-mcp', version: '1.0.0' }, { capabilities: { tools: {} } });

async function supabaseRequest(endpoint: string, method: string = 'GET', body?: unknown, headers?: Record<string, string>) {
  const response = await fetch(`${SUPABASE_URL}${endpoint}`, {
    method,
    headers: {
      'apikey': SUPABASE_KEY,
      'Authorization': `Bearer ${SUPABASE_KEY}`,
      'Content-Type': 'application/json',
      'Prefer': 'return=representation',
      ...headers,
    },
    body: body ? JSON.stringify(body) : undefined,
  });
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.message || error.error || 'Supabase API error');
  }
  const text = await response.text();
  return text ? JSON.parse(text) : {};
}

const tools = [
  { name: 'query_table', description: 'Query data from a table', inputSchema: { type: 'object', properties: { table: { type: 'string' }, select: { type: 'string' }, filter: { type: 'string' }, order: { type: 'string' }, limit: { type: 'number' } }, required: ['table'] } },
  { name: 'insert_rows', description: 'Insert rows', inputSchema: { type: 'object', properties: { table: { type: 'string' }, data: { type: 'array' }, upsert: { type: 'boolean' } }, required: ['table', 'data'] } },
  { name: 'update_rows', description: 'Update rows', inputSchema: { type: 'object', properties: { table: { type: 'string' }, data: { type: 'object' }, filter: { type: 'string' } }, required: ['table', 'data', 'filter'] } },
  { name: 'delete_rows', description: 'Delete rows', inputSchema: { type: 'object', properties: { table: { type: 'string' }, filter: { type: 'string' } }, required: ['table', 'filter'] } },
  { name: 'run_rpc', description: 'Call a database function', inputSchema: { type: 'object', properties: { function_name: { type: 'string' }, params: { type: 'object' } }, required: ['function_name'] } },
  { name: 'upload_file', description: 'Upload a file', inputSchema: { type: 'object', properties: { bucket: { type: 'string' }, path: { type: 'string' }, file_base64: { type: 'string' }, content_type: { type: 'string' } }, required: ['bucket', 'path', 'file_base64'] } },
  { name: 'list_files', description: 'List files', inputSchema: { type: 'object', properties: { bucket: { type: 'string' }, path: { type: 'string' }, limit: { type: 'number' } }, required: ['bucket'] } },
];

server.setRequestHandler(ListToolsRequestSchema, async () => ({ tools }));

server.setRequestHandler(CallToolRequestSchema, async (request) => {
  const { name, arguments: args } = request.params;
  try {
    let result;
    const a = args as Record<string, unknown>;
    switch (name) {
      case 'query_table': {
        let url = `/rest/v1/${a.table}?select=${a.select || '*'}`;
        if (a.filter) url += `&${a.filter}`;
        if (a.order) url += `&order=${a.order}`;
        if (a.limit) url += `&limit=${a.limit}`;
