      case 'run_rpc': result = await supabaseRequest(`/rest/v1/rpc/${a.function_name}`, 'POST', a.params || {}); break;
      case 'list_files': result = await supabaseRequest(`/storage/v1/object/list/${a.bucket}`, 'POST', { prefix: a.path || '', limit: a.limit || 100 }); break;
