import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';

const VERCEL_TOKEN = process.env.VERCEL_TOKEN;
const VERCEL_BASE_URL = 'https://api.vercel.com';

if (!VERCEL_TOKEN) {
  console.error('Error: VERCEL_TOKEN environment variable is required');
  process.exit(1);
}

const server = new Server({ name: 'vercel-mcp', version: '1.0.0' }, { capabilities: { tools: {} } });

async function vercelRequest(endpoint: string, method: string = 'GET', body?: unknown) {
  const response = await fetch(`${VERCEL_BASE_URL}${endpoint}`, {
    method,
    headers: {
      'Authorization': `Bearer ${VERCEL_TOKEN}`,
      'Content-Type': 'application/json',
    },
    body: body ? JSON.stringify(body) : undefined,
  });
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.error?.message || 'Vercel API error');
  }
  return response.json();
}

const tools = [
  { name: 'list_deployments', description: 'List deployments', inputSchema: { type: 'object', properties: { projectId: { type: 'string' }, limit: { type: 'number' }, state: { type: 'string' }, target: { type: 'string' } } } },
  { name: 'get_deployment', description: 'Get deployment', inputSchema: { type: 'object', properties: { deploymentId: { type: 'string' } }, required: ['deploymentId'] } },
  { name: 'list_projects', description: 'List projects', inputSchema: { type: 'object', properties: { limit: { type: 'number' }, search: { type: 'string' } } } },
  { name: 'get_project', description: 'Get project', inputSchema: { type: 'object', properties: { projectId: { type: 'string' } }, required: ['projectId'] } },
  { name: 'list_domains', description: 'List domains', inputSchema: { type: 'object', properties: { projectId: { type: 'string' }, limit: { type: 'number' } } } },
  { name: 'add_domain', description: 'Add domain', inputSchema: { type: 'object', properties: { projectId: { type: 'string' }, domain: { type: 'string' } }, required: ['projectId', 'domain'] } },
  { name: 'get_env_vars', description: 'Get env vars', inputSchema: { type: 'object', properties: { projectId: { type: 'string' } }, required: ['projectId'] } },
];

server.setRequestHandler(ListToolsRequestSchema, async () => ({ tools }));

server.setRequestHandler(CallToolRequestSchema, async (request) => {
  const { name, arguments: args } = request.params;
  try {
    let result;
    const a = args as Record<string, unknown>;
    switch (name) {
      case 'list_deployments': result = await vercelRequest(`/v6/deployments${a.projectId ? `?projectId=${a.projectId}` : ''}`); break;
      case 'get_deployment': result = await vercelRequest(`/v13/deployments/${a.deploymentId}`); break;
      case 'list_projects': result = await vercelRequest('/v9/projects'); break;
      case 'get_project': result = await vercelRequest(`/v9/projects/${a.projectId}`); break;
      case 'list_domains': result = await vercelRequest(`/v5/projects/${a.projectId}/domains`); break;
      case 'add_domain': result = await vercelRequest(`/v10/projects/${a.projectId}/domains`, 'POST', { name: a.domain }); break;
      case 'get_env_vars': result = await vercelRequest(`/v9/projects/${a.projectId}/env`); break;
      default: throw new Error(`Unknown tool: ${name}`);
    }
    return { content: [{ type: 'text', text: JSON.stringify(result, null, 2) }] };
  } catch (error) {
    return { content: [{ type: 'text', text: `Error: ${error instanceof Error ? error.message : 'Unknown error'}` }], isError: true };
  }
});

async function main() {
  const transport = new StdioServerTransport();
  await server.connect(transport);
}

main().catch(console.error);
