import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';

const SLACK_TOKEN = process.env.SLACK_TOKEN;
const SLACK_BASE_URL = 'https://slack.com/api';

if (!SLACK_TOKEN) {
  console.error('Error: SLACK_TOKEN environment variable is required');
  process.exit(1);
}

const server = new Server({ name: 'slack-mcp', version: '1.0.0' }, { capabilities: { tools: {} } });

async function slackRequest(method: string, params: Record<string, unknown> = {}) {
  const response = await fetch(`${SLACK_BASE_URL}/${method}`, {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${SLACK_TOKEN}`,
      'Content-Type': 'application/json; charset=utf-8',
    },
    body: JSON.stringify(params),
  });
  const result = await response.json();
  if (!result.ok) throw new Error(result.error || 'Slack API error');
  return result;
}

const tools = [
  { name: 'post_message', description: 'Send a message to a channel', inputSchema: { type: 'object', properties: { channel: { type: 'string' }, text: { type: 'string' }, blocks: { type: 'array' }, thread_ts: { type: 'string' } }, required: ['channel', 'text'] } },
  { name: 'list_channels', description: 'List all channels', inputSchema: { type: 'object', properties: { types: { type: 'string' }, limit: { type: 'number' }, cursor: { type: 'string' } } } },
  { name: 'get_channel_info', description: 'Get channel information', inputSchema: { type: 'object', properties: { channel: { type: 'string' } }, required: ['channel'] } },
  { name: 'create_channel', description: 'Create a new channel', inputSchema: { type: 'object', properties: { name: { type: 'string' }, is_private: { type: 'boolean' } }, required: ['name'] } },
  { name: 'list_users', description: 'List all users', inputSchema: { type: 'object', properties: { limit: { type: 'number' }, cursor: { type: 'string' } } } },
  { name: 'get_user_info', description: 'Get user information', inputSchema: { type: 'object', properties: { user: { type: 'string' } }, required: ['user'] } },
  { name: 'add_reaction', description: 'Add a reaction', inputSchema: { type: 'object', properties: { channel: { type: 'string' }, timestamp: { type: 'string' }, name: { type: 'string' } }, required: ['channel', 'timestamp', 'name'] } },
  { name: 'get_conversation_history', description: 'Get message history', inputSchema: { type: 'object', properties: { channel: { type: 'string' }, limit: { type: 'number' }, oldest: { type: 'string' }, latest: { type: 'string' } }, required: ['channel'] } },
  { name: 'search_messages', description: 'Search for messages', inputSchema: { type: 'object', properties: { query: { type: 'string' }, count: { type: 'number' }, sort: { type: 'string' } }, required: ['query'] } },
];

server.setRequestHandler(ListToolsRequestSchema, async () => ({ tools }));

server.setRequestHandler(CallToolRequestSchema, async (request) => {
  const { name, arguments: args } = request.params;
  try {
    let result;
    const a = args as Record<string, unknown>;
    switch (name) {
      case 'post_message': result = await slackRequest('chat.postMessage', a); break;
      case 'list_channels': result = await slackRequest('conversations.list', a); break;
      case 'get_channel_info': result = await slackRequest('conversations.info', a); break;
      case 'create_channel': result = await slackRequest('conversations.create', a); break;
      case 'list_users': result = await slackRequest('users.list', a); break;
      case 'get_user_info': result = await slackRequest('users.info', a); break;
      case 'add_reaction': result = await slackRequest('reactions.add', a); break;
      case 'get_conversation_history': result = await slackRequest('conversations.history', a); break;
      case 'search_messages': result = await slackRequest('search.messages', a); break;
      default: throw new Error(`Unknown tool: ${name}`);
    }
    return { content: [{ type: 'text', text: JSON.stringify(result, null, 2) }] };
  } catch (error) {
    return { content: [{ type: 'text', text: `Error: ${error instanceof Error ? error.message : 'Unknown error'}` }], isError: true };
