#!/usr/bin/env python3
"""Stripe MCP Server - Payment processing tools"""

import os
import json
import httpx
from mcp.server import Server
from mcp.server.stdio import stdio_server
from mcp.types import Tool, TextContent

STRIPE_API_KEY = os.environ.get('STRIPE_API_KEY')
STRIPE_BASE_URL = 'https://api.stripe.com/v1'

if not STRIPE_API_KEY:
    raise ValueError('STRIPE_API_KEY environment variable is required')

server = Server('stripe-mcp')

async def stripe_request(endpoint: str, method: str = 'GET', data: dict = None):
    async with httpx.AsyncClient() as client:
        headers = {
            'Authorization': f'Bearer {STRIPE_API_KEY}',
            'Content-Type': 'application/x-www-form-urlencoded',
        }
        url = f'{STRIPE_BASE_URL}{endpoint}'

        if method == 'GET':
            response = await client.get(url, headers=headers, params=data)
        elif method == 'POST':
            response = await client.post(url, headers=headers, data=data)
        elif method == 'DELETE':
            response = await client.delete(url, headers=headers, data=data)

        response.raise_for_status()
        return response.json()

@server.list_tools()
async def list_tools():
    return [
        Tool(name='create_customer', description='Create a new Stripe customer',
             inputSchema={'type': 'object', 'properties': {'email': {'type': 'string'}, 'name': {'type': 'string'}}, 'required': ['email']}),
        Tool(name='get_customer', description='Retrieve a customer by ID',
             inputSchema={'type': 'object', 'properties': {'customer_id': {'type': 'string'}}, 'required': ['customer_id']}),
        Tool(name='create_payment_intent', description='Create a payment intent',
             inputSchema={'type': 'object', 'properties': {'amount': {'type': 'number'}, 'currency': {'type': 'string'}}, 'required': ['amount', 'currency']}),
        Tool(name='get_balance', description='Get account balance',
             inputSchema={'type': 'object', 'properties': {}}),
    ]

@server.call_tool()
async def call_tool(name: str, arguments: dict):
    try:
        if name == 'create_customer':
            result = await stripe_request('/customers', 'POST', arguments)
        elif name == 'get_customer':
            result = await stripe_request(f"/customers/{arguments['customer_id']}")
        elif name == 'create_payment_intent':
            result = await stripe_request('/payment_intents', 'POST', arguments)
        elif name == 'get_balance':
            result = await stripe_request('/balance')
        else:
            raise ValueError(f'Unknown tool: {name}')

        return [TextContent(type='text', text=json.dumps(result, indent=2))]
    except Exception as e:
        return [TextContent(type='text', text=f'Error: {str(e)}')]

async def main():
    async with stdio_server() as (read_stream, write_stream):
        await server.run(read_stream, write_stream, server.create_initialization_options())

if __name__ == '__main__':
    import asyncio
    asyncio.run(main())
