import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';

const TWILIO_ACCOUNT_SID = process.env.TWILIO_ACCOUNT_SID;
const TWILIO_AUTH_TOKEN = process.env.TWILIO_AUTH_TOKEN;

if (!TWILIO_ACCOUNT_SID || !TWILIO_AUTH_TOKEN) {
  console.error('Error: TWILIO_ACCOUNT_SID and TWILIO_AUTH_TOKEN environment variables are required');
  process.exit(1);
}

const server = new Server({ name: 'twilio-mcp', version: '1.0.0' }, { capabilities: { tools: {} } });

async function twilioRequest(endpoint: string, method: string = 'GET', body?: Record<string, string>) {
  const auth = Buffer.from(`${TWILIO_ACCOUNT_SID}:${TWILIO_AUTH_TOKEN}`).toString('base64');
  const response = await fetch(`https://api.twilio.com/2010-04-01/Accounts/${TWILIO_ACCOUNT_SID}${endpoint}`, {
    method,
    headers: {
      'Authorization': `Basic ${auth}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    },
    body: body ? new URLSearchParams(body).toString() : undefined,
  });
  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.message || 'Twilio API error');
  }
  return response.json();
}

const tools = [
  { name: 'send_sms', description: 'Send an SMS', inputSchema: { type: 'object', properties: { to: { type: 'string' }, from: { type: 'string' }, body: { type: 'string' }, status_callback: { type: 'string' } }, required: ['to', 'from', 'body'] } },
  { name: 'list_messages', description: 'List messages', inputSchema: { type: 'object', properties: { to: { type: 'string' }, from: { type: 'string' }, date_sent: { type: 'string' }, page_size: { type: 'number' } } } },
  { name: 'get_message', description: 'Get a message', inputSchema: { type: 'object', properties: { message_sid: { type: 'string' } }, required: ['message_sid'] } },
  { name: 'make_call', description: 'Make a call', inputSchema: { type: 'object', properties: { to: { type: 'string' }, from: { type: 'string' }, url: { type: 'string' }, twiml: { type: 'string' } }, required: ['to', 'from'] } },
  { name: 'list_calls', description: 'List calls', inputSchema: { type: 'object', properties: { to: { type: 'string' }, from: { type: 'string' }, status: { type: 'string' }, page_size: { type: 'number' } } } },
  { name: 'list_phone_numbers', description: 'List phone numbers', inputSchema: { type: 'object', properties: { country_code: { type: 'string' }, type: { type: 'string' }, page_size: { type: 'number' } } } },
  { name: 'send_whatsapp', description: 'Send WhatsApp message', inputSchema: { type: 'object', properties: { to: { type: 'string', description: 'WhatsApp number with whatsapp: prefix' }, from: { type: 'string' }, body: { type: 'string' } }, required: ['to', 'from', 'body'] } },
];

server.setRequestHandler(ListToolsRequestSchema, async () => ({ tools }));

server.setRequestHandler(CallToolRequestSchema, async (request) => {
  const { name, arguments: args } = request.params;
  try {
    let result;
    const a = args as Record<string, string>;
    switch (name) {
      case 'send_sms': result = await twilioRequest('/Messages.json', 'POST', { To: a.to, From: a.from, Body: a.body }); break;
      case 'list_messages': result = await twilioRequest('/Messages.json'); break;
      case 'get_message': result = await twilioRequest(`/Messages/${a.message_sid}.json`); break;
      case 'make_call': result = await twilioRequest('/Calls.json', 'POST', { To: a.to, From: a.from, Url: a.url || '', Twiml: a.twiml || '' }); break;
      case 'list_calls': result = await twilioRequest('/Calls.json'); break;
      case 'list_phone_numbers': result = await twilioRequest('/IncomingPhoneNumbers.json'); break;
      case 'send_whatsapp': result = await twilioRequest('/Messages.json', 'POST', { To: a.to, From: a.from, Body: a.body }); break;
      default: throw new Error(`Unknown tool: ${name}`);
    }
    return { content: [{ type: 'text', text: JSON.stringify(result, null, 2) }] };
  } catch (error) {
    return { content: [{ type: 'text', text: `Error: ${error instanceof Error ? error.message : 'Unknown error'}` }], isError: true };
  }
});

async function main() {
  const transport = new StdioServerTransport();
  await server.connect(transport);
}

main().catch(console.error);
